import Header from '~/pages/header/Header';
import Footer from '~/components/footer/Footer';

import React, { useState, useEffect } from 'react';
import './carts.scss';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
    fa1,
    fa2,
    fa3,
    faCartShopping,
    faCheck,
    faCircle,
    faMoneyBill1Wave,
    faSpa,
    faStar,
    faTree,
} from '@fortawesome/free-solid-svg-icons';
import { useLocation, useNavigate } from 'react-router-dom';

function Carts({ isLoggedIn, username, id}) {
    // khai báo api lấy các sản phẩm trong giỏ hàng
    const cartsAPI = 'http://localhost:3000/cart_bought';
    const [carts, setCarts] = useState([]);
    const [totalMoney, setTotalMoney] = useState(0);
    // tạo kết nối
    useEffect(() => {
        fetch(cartsAPI)
            .then((response) => response.json())
            .then((data) => {
                setCarts(data);
                const filteredCarts = data.filter((cart) => cart.id === idToFind);
                const newQuantities = {};
                data.forEach((cart) => {
                    newQuantities[cart.id] = cart.quantity; // Lưu quantity theo cart id
                });

                setQuantities(newQuantities); // Cập nhật state quantities
            });
    }, [carts.length]);
    const [quantities, setQuantities] = useState([]);

    useEffect(() => {
        updateTotalMoney();
    }, [quantities]);

    const updateTotalMoney = () => {
        let total = 0;
        carts.forEach((cart) => {
            const quantity = quantities[cart.id] || cart.quantity;
            total += cart.price_bought * quantity;
        });
        setTotalMoney(total);
    };

    // const [quantities, setQuantities] = useState([1, 1]);
    // const [totalMoney, setTotalMoney] = useState(0);
    // const decreaseQuantity = (index) => {
    //     const newQuantities = [...quantities];
    //     if (newQuantities[index] > 1) {
    //         newQuantities[index] -= 1;
    //         setQuantities(newQuantities);
    //     }
    // };

    // const increaseQuantity = (index) => {
    //     const newQuantities = [...quantities];
    //     newQuantities[index] += 1;
    //     setQuantities(newQuantities);
    // };

    // const decreaseQuantity = (id) => {
    //     if (quantities[id] > 1) {
    //         setQuantities((prevQuantities) => ({
    //             ...prevQuantities,
    //             [id]: prevQuantities[id] - 1,
    //         }));
    //     }
    // };

    // const increaseQuantity = (id) => {
    //     setQuantities((prevQuantities) => ({
    //         ...prevQuantities,
    //         [id]: prevQuantities[id] ? prevQuantities[id] + 1 : 1,
    //     }));
    // };

    const decreaseQuantity = (id, cart) => {
        if (quantities[id] > 0) {
            setQuantities((prevQuantities) => ({
                ...prevQuantities,
                [id]: prevQuantities[id] - 1,
            }));

            fetch(`${cartsAPI}/${cart.id}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    image_represent: cart.image_represent,
                    name_product: cart.name_product,
                    price_bought: cart.price_bought,
                    id: cart.id,
                    quantity: quantities[id] - 1,
                }),
            })
                .then((response) => {
                    if (response.ok) {
                        // Tăng sản phẩm lên 1
                        // setQuantities((prevQuantities) => ({
                        //     ...prevQuantities,
                        //     [id]: prevQuantities[id] + 1,
                        //   }));
                    } else {
                        throw new Error('Có lỗi xảy ra khi tăng số lượng sản phẩm.');
                    }
                })
                .catch((error) => console.error('Error deleting product:', error));
        }
    };

    const increaseQuantity = (id, cart) => {
        setQuantities((prevQuantities) => ({
            ...prevQuantities,
            [id]: (prevQuantities[id] || 0) + 1,
        }));

        fetch(`${cartsAPI}/${cart.id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                image_represent: cart.image_represent,
                name_product: cart.name_product,
                price_bought: cart.price_bought,
                id: cart.id,
                quantity: quantities[id] + 1,
            }),
        })
            .then((response) => {
                if (response.ok) {
                    // Tăng sản phẩm lên 1
                    // setQuantities((prevQuantities) => ({
                    //     ...prevQuantities,
                    //     [id]: prevQuantities[id] + 1,
                    //   }));
                } else {
                    throw new Error('Có lỗi xảy ra khi tăng số lượng sản phẩm.');
                }
            })
            .catch((error) => console.error('Error deleting product:', error));
    };

    // const updateTotalMoney = () => {
    //     let total = 0;
    //     carts.forEach((cart, index) => {
    //       const quantity = quantities[index] || 1;
    //       total += cart.price_bought * quantity;
    //     });
    //     setTotalMoney(total);
    //   };

    // xử lý thanh toán
    const orderAPI = 'http://localhost:3000/order';
    const history = useNavigate();
    const handlePay = (carts, total_money) => {
        // Thực hiện các thao tác để insert dữ liệu vào data ở đây
        const totalMoney = carts.reduce((total, cart) => total + cart.price_bought * quantities[cart.id], 0);

        const newOther = {
            carts: carts.map((cart) => ({
                id: cart.id,
                image_represent: cart.image_represent,
                name_product: cart.name_product,
                price_bought: cart.price_bought,
                quantity: quantities[cart.id],
            })),
            total_money: totalMoney,
        };

        fetch(orderAPI, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(newOther),
        })
            .then((response) => response.json())
            .then((data) => {
                setCarts([...carts, data]);
            })
            .catch((error) => console.error('Error creating post:', error));

        // Chuyển hướng đến trang thanh toán
        history('/pay');
    };

    return (
        <>
            {isLoggedIn ? (
                <></>
            ) : (
                <>
                    <Header isLoggedIn={isLoggedIn} username={username} />
                </>
            )}

            <div className="section-first-title">
                <FontAwesomeIcon icon={faTree} />
                <h3>Thông tin giỏ hàng</h3>
                <FontAwesomeIcon icon={faTree} />
                <div className="icon-mid">
                    <FontAwesomeIcon icon={faSpa} />
                </div>
            </div>

            <div class="container_list_carts">
                <div class="top_infomation">
                    <div class="stages_of_cart">
                        <div class="stage_1">
                            <FontAwesomeIcon className="phase" icon={faCartShopping} />
                            <p>Giỏ hàng của tôi</p>
                            <FontAwesomeIcon className="fa-light" icon={fa1} />
                        </div>
                        <div class="stage_2">
                            <FontAwesomeIcon className="phase" icon={faMoneyBill1Wave} />
                            <p>Giỏ hàng của tôi</p>
                            <FontAwesomeIcon icon={fa2} />
                        </div>
                        <div class="stage_3">
                            <FontAwesomeIcon className="phase" icon={faCheck} />
                            <p>Giỏ hàng của tôi</p>
                            <FontAwesomeIcon icon={fa3} />
                        </div>
                    </div>
                </div>
                <div class="bottom_infomation">
                    <div class="section_products_cart">
                        <table class="list_products">
                            <tr>
                                <th></th>
                                <th>Tên sản phẩm</th>
                                <th>Giá cả</th>
                                <th>Số lượng</th>
                                <th>Thành tiền</th>
                            </tr>
                            {carts.map((cart) => (
                                <>
                                    <tr key={cart.id}>
                                        <td>
                                            <img src={cart.image_represent} />
                                        </td>
                                        <td>{cart.name_product}</td>
                                        <td>{cart.price_bought}</td>
                                        <td>
                                            <div className="quantity">
                                                <button
                                                    className="btn_minus"
                                                    onClick={() => decreaseQuantity(cart.id, cart)}
                                                >
                                                    -
                                                </button>

                                                <span className="quantity_value">{quantities[cart.id]}</span>
                                                <button
                                                    className="btn_plus"
                                                    onClick={() => increaseQuantity(cart.id, cart)}
                                                >
                                                    +
                                                </button>
                                            </div>
                                        </td>
                                        <td className="total_money">{cart.price_bought * quantities[cart.id]}</td>
                                    </tr>
                                </>
                            ))}
                        </table>
                        <div className="total_money_cart">
                            <p className="result">
                                Tổng tiền: <span>{totalMoney}</span>
                            </p>
                            <button className="continute_buy_product">Tiếp tục mua hàng</button>
                            <button className="pay" onClick={() => handlePay(carts)}>
                                Thanh toán
                            </button>
                        </div>
                    </div>
                </div>
            </div>
            <Footer />
        </>
    );
}

export default Carts;
