import Header from '~/pages/header/Header';
import Footer from '~/components/footer/Footer';

import React, { useState, useEffect } from 'react';
import './info.scss';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
    fa1,
    fa2,
    fa3,
    faCartShopping,
    faCheck,
    faChevronRight,
    faCircle,
    faMoneyBill1Wave,
    faSpa,
    faStar,
    faTree,
} from '@fortawesome/free-solid-svg-icons';
import { useLocation, useNavigate } from 'react-router-dom';

function Info({ isLoggedIn, username, id }) {
    const [clients, setClients] = useState([]);
    useEffect(() => {
        fetch(`http://localhost:8080/webplant_api/accounts_api/read.php?id_account=${id}`)
            .then((response) => response.json())
            .then((data) => {
                setClients(data);
                // console.log(clients);
            });
    }, [id]);

    const [orders, setOrders] = useState([]);

    useEffect(() => {
        fetch(`http://localhost:8080/webplant_api/orders_api/read.php?id_owner=${id}`)
            .then((response) => response.json())
            .then((data) => {
                setOrders(data);
                console.log(data);
            });
    }, []); // Chỉ kích hoạt useEffect một lần sau khi component được render

    return (
        <>
            {isLoggedIn ? (
                <></>
            ) : (
                <>
                    <Header isLoggedIn={isLoggedIn} username={username} />
                </>
            )}

            <div className="section_info_client">
                <img src="Anh_Dao.jpg"></img>
                <div className="content_info">
                    <h3 className="title_info">Thông tin khách hàng</h3>
                    {clients.map((client) =>
                        client.username_account === username ? (
                            <>
                                <div className="info_detail">
                                    <h3>Tên khách hàng:</h3>
                                    <label>{client.name_client}</label>
                                </div>
                                <div className="info_detail">
                                    <h3>Địa chỉ khách hàng:</h3>
                                    <label>{client.address_client}</label>
                                </div>
                                <div className="info_detail">
                                    <h3>Số điện thoại:</h3>
                                    <label>{client.phone_client}</label>
                                </div>
                                <div className="info_detail">
                                    <h3>Email:</h3>
                                    <label>{client.email_client}</label>
                                </div>
                            </>
                        ) : null,
                    )}
                </div>
            </div>

            {orders.length === 0 ? (
                <p>Không có đơn hàng nào.</p>
            ) : (
                orders.map((order, index) => (
                    <div key={order.id_order} className="section_info_order">
                        <div className="info_order_left">
                            {index === 0 || orders[index - 1].id_order !== order.id_order ? (
                                <>
                                    <h3>{order.id_order}</h3>
                                    <div className="status">
                                        <p>Ngày lập đơn: {order.day_bought}</p>
                                        <button className="btn_status">{order.status_order}</button>
                                        <button className="btn_delete">Hủy đơn</button>
                                    </div>
                                </>
                            ) : null}
                        </div>
               
                            <div className="info_order_right">
                                <h3 className="info_introduce">Chi tiết sản phẩm đơn hàng</h3>
                                <table className="list_products">
                                    <thead>
                                        <tr>
                                            <th>Ảnh sản phẩm</th>
                                            <th>Tên sản phẩm</th>
                                            <th>Giá sản phẩm</th>
                                            <th>Số lượng</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        <tr key={order.id_order}>
                                            <td>
                                                <img src={order.image_represent} alt="Product" />
                                            </td>
                                            <td>{order.name_product}</td>
                                            <td>{order.price_bought}</td>
                                            <td>{order.quantity}</td>
                                        </tr>
                                    </tbody>
                                </table>
                            </div>
                        
                    </div>
                ))
            )}

            <Footer />
        </>
    );
}

export default Info;
