import Header from './pages/header/Header';
import './App.css';
import { Fragment } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import React, { useState, useEffect } from 'react';
import ContactPage from './pages/Contact';
import Carts from './pages/carts/Carts.js';
import Pay from './pages/pay/Pay';
import Body from './components/Body';
import Footer from './components/footer/Footer';
import Login from './pages/login/Login';
import Register from './pages/register/Register';

function App() {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [username, setUsername] = useState('');
    const [idFlag, setIdFlag] = useState('');

    const handleLogin = (username, id) => {
        setIsLoggedIn(true);
        setUsername(username);
        setIdFlag(id);
        alert(id + 's');

        localStorage.setItem('isLoggedIn', true);
        localStorage.setItem('idFlag', id);
        localStorage.setItem('username', username);

    };

    // useEffect(() => {
    //     // setIdFlag(id);
    //     alert(idFlag);
    //   }, [idFlag])

    const handleLogout = () => {
        setIsLoggedIn(false);
        setUsername('');
        localStorage.removeItem('isLoggedIn');
        localStorage.removeItem('username');
    };

    useEffect(() => {
        const storedIsLoggedIn = localStorage.getItem('isLoggedIn');
        const storedUsername = localStorage.getItem('username');
        const storedIdFlag = localStorage.getItem('idFlag');
        if (storedIsLoggedIn && storedUsername) {
            setIsLoggedIn(true);
            setUsername(storedUsername);
            setIdFlag(storedIdFlag);
        }
    }, []);
    return (
        <Router>
            {isLoggedIn ? (
                <>
                    <Header isLoggedIn={isLoggedIn} username={username} onLogout={handleLogout} />
                    <div className="App">
                        <Routes>
                            <Route path="/" element={[<Body username={username} id={idFlag} />, <Footer />]} />
                            <Route
                                path="/contact"
                                element={<ContactPage isLoggedIn={isLoggedIn} username={username} />}
                            />
                            <Route path="/carts" element={<Carts isLoggedIn={isLoggedIn} username={username} />} />
                            <Route path="/pay" element={<Pay isLoggedIn={isLoggedIn} username={username} />} />
                            <Route path="/login" element={<Login onLogin={handleLogin} />} />
                            <Route path="/register" />
                        </Routes>
                    </div>
                </>
            ) : (
                <>
                    <div className="App">
                        <Routes>
                            <Route
                                path="/"
                                element={[
                                    <Header isLoggedIn={isLoggedIn} username={username} onLogout={handleLogout} />,
                                    <Body username={username} id={idFlag} />,
                                    <Footer />,
                                ]}
                            />
                            <Route
                                path="/contact"
                                element={<ContactPage isLoggedIn={isLoggedIn} username={username} />}
                            />
                            <Route path="/login" element={<Login onLogin={handleLogin} />} />
                            <Route path="/register" element={<Register />} />
                        </Routes>
                    </div>
                </>
            )}
        </Router>
    );
}

export default App;
