import Header from '~/pages/header/Header';
import Footer from '~/components/footer/Footer';
import './pay.scss';
import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
    fa1,
    fa2,
    fa3,
    faCartShopping,
    faCheck,
    faCircle,
    faMoneyBill1Wave,
    faSpa,
    faStar,
    faTree,
} from '@fortawesome/free-solid-svg-icons';

function Pay({ isLoggedIn, username, id }) {
    //tạo kết nối lấy thông tin khách hàng
    const clientAPI = `http://localhost:3000/accounts?id=${id}`;
    const [clients, setClients] = useState();
    useEffect(() => {
        fetch(clientAPI)
            .then((response) => response.json())
            .then((data) => {
                setClients(data);
            });
    }, [id]);

    const orderAPI = `http://localhost:3000/order?owner=${id}`;
    const [orders, setOrders] = useState([]);
    // tạo kết nối
    useEffect(() => {
        fetch(orderAPI)
            .then((response) => response.json())
            .then((data) => {
                setOrders(data);
            });
    }, [orders.length]);

    const filteredOrders = orders.filter((order) => order.owner === id);

    return (
        <>
            {isLoggedIn ? (
                <></>
            ) : (
                <>
                    <Header isLoggedIn={isLoggedIn} username={username} />
                </>
            )}

            <div className="section-first-title">
                <FontAwesomeIcon icon={faTree} />
                <h3>Thông tin thanh toán</h3>
                <FontAwesomeIcon icon={faTree} />
                <div className="icon-mid">
                    <FontAwesomeIcon icon={faSpa} />
                </div>
            </div>

            <div className="section_pay_money">
                <div class="top_infomation">
                    <div class="stages_of_cart">
                        <div class="stage_1">
                            <FontAwesomeIcon className="phase" icon={faCartShopping} />
                            <p>Giỏ hàng của tôi</p>
                            <FontAwesomeIcon className="fa-light" icon={fa1} />
                        </div>
                        <div class="stage_2">
                            <FontAwesomeIcon className="phase" icon={faMoneyBill1Wave} />
                            <p>Giỏ hàng của tôi</p>
                            <FontAwesomeIcon icon={fa2} />
                        </div>
                        <div class="stage_3">
                            <FontAwesomeIcon className="phase" icon={faCheck} />
                            <p>Giỏ hàng của tôi</p>
                            <FontAwesomeIcon icon={fa3} />
                        </div>
                    </div>
                </div>
                <div className="infomation_client_pay">
                    <form>
                        <h3>Thông tin khách hàng</h3>
                        <div class="form-group">
                            <input placeholder="Tên khách hàng" type="text" id="name" name="name" required />
                        </div>
                        <div class="form-group">
                            <input placeholder="SĐT" type="text" id="name" name="name" required />
                        </div>
                        <div class="form-group">
                            <input placeholder="Email" type="text" id="name" name="name" required />
                        </div>
                        <div class="form-group">
                            <input placeholder="Địa chỉ" type="text" id="address" name="address" required />
                        </div>
                        <h3>Thông tin đơn hàng</h3>
                        <table class="list_products">
                            {filteredOrders.map((order) => (
                                {
                                    
                                }
                            ))}
                        </table>
                        <button type="submit">Xác nhận đặt hàng</button>
                    </form>
                </div>
            </div>
            <Footer />
        </>
    );
}

export default Pay;
