import Header from './components/header/Header';
import './App.css';
import { Fragment } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import React, { useState, useEffect } from 'react';
import ContactPage from "./pages/contactPage/Contact"
import Body from './components/Body';
import Footer from './components/footer/Footer';
import Login from './pages/loginPage/Login';
function App() {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [username, setUsername] = useState('');

    // const handleLogin = (username) => {
    //     setIsLoggedIn(true);
    //     setUsername(username);
    //     localStorage.setItem('isLoggedIn', true);
    //     localStorage.setItem('username', username);
    //   };

      const handleLogout = () => {
        setIsLoggedIn(false);
        setUsername('');
        localStorage.removeItem('isLoggedIn');
        localStorage.removeItem('username');
      };

      useEffect(() => {
        const storedIsLoggedIn = localStorage.getItem('isLoggedIn');
        const storedUsername = localStorage.getItem('username');
        if (storedIsLoggedIn && storedUsername) {
          setIsLoggedIn(true);
          setUsername(storedUsername);
        }
      }, []);

    return (
        <Router>

            <div className="App">
                <Routes>     
                {/* <Header isLoggedIn={isLoggedIn} username={username} />              */}
                <Route path="/" element={[<Header isLoggedIn={isLoggedIn} username={username} onLogout={handleLogout}/>, <Body/>, <Footer />] }/>
                <Route path="/contact" element={<ContactPage isLoggedIn={isLoggedIn} username={username}/>}/>
                <Route path="/login" element={<Login />}/>
                </Routes>
            </div>
        </Router>
    );
}

export default App;
