import Header from '~/pages/header/Header';
import Footer from '~/components/footer/Footer';

import React, { useState, useEffect } from 'react';
import './info.scss';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
    fa1,
    fa2,
    fa3,
    faCartShopping,
    faCheck,
    faChevronRight,
    faCircle,
    faMoneyBill1Wave,
    faSpa,
    faStar,
    faTree,
} from '@fortawesome/free-solid-svg-icons';
import { useLocation, useNavigate } from 'react-router-dom';

function Info({ isLoggedIn, username, id }) {
    const [clients, setClients] = useState([]);
    useEffect(() => {
        fetch(`http://localhost:8080/webplant_api/accounts_api/read.php?id_account=${id}`)
            .then((response) => response.json())
            .then((data) => {
                setClients(data);
                // console.log(clients);
            });
    }, [id]);

    const [orders, setOrders] = useState([]);

    useEffect(() => {
        fetch(`http://localhost:8080/webplant_api/orders_api/read.php?id_owner=${id}`)
            .then((response) => response.json())
            .then((data) => {
                setOrders(data);
                console.log(data);
            });
    }, []); // Chỉ kích hoạt useEffect một lần sau khi component được render

    const handleClickDeleteOrder = (order_id) => {
        fetch('http://localhost:8080/webplant_api/order_api/read.php')
            .then((response) => response.json())
            .then((data) => {
                const existingOrder = data.find((item) => item.id_order === order_id);
    
                if (existingOrder) {
                    // Nếu tìm thấy đơn hàng, bạn có thể xử lý xóa ở đây
                    // Ví dụ: gọi hàm để xóa đơn hàng
                    deleteOrder(existingOrder);
                } else {
                    console.log('Không tìm thấy đơn hàng cần xóa');
                }
            })
            .catch((error) => {
                console.error('Lỗi khi truy vấn API:', error);
            });
    };
    
    // Hàm xử lý xóa đơn hàng
    const deleteOrder = (order) => {
        // Gọi API hoặc thực hiện các thao tác xóa đơn hàng ở đây
        console.log('Xóa đơn hàng:', order);
    };
    

    return (
        <>
            {isLoggedIn ? (
                <></>
            ) : (
                <>
                    <Header isLoggedIn={isLoggedIn} username={username} />
                </>
            )}

            <div className="section_info_client">
                <img src="Anh_Dao.jpg"></img>
                <div className="content_info">
                    <h3 className="title_info">Thông tin khách hàng</h3>
                    {clients.map((client) =>
                        client.username_account === username ? (
                            <>
                                <div className="info_detail">
                                    <h3>Tên khách hàng:</h3>
                                    <label>{client.name_client}</label>
                                </div>
                                <div className="info_detail">
                                    <h3>Địa chỉ khách hàng:</h3>
                                    <label>{client.address_client}</label>
                                </div>
                                <div className="info_detail">
                                    <h3>Số điện thoại:</h3>
                                    <label>{client.phone_client}</label>
                                </div>
                                <div className="info_detail">
                                    <h3>Email:</h3>
                                    <label>{client.email_client}</label>
                                </div>
                            </>
                        ) : null,
                    )}
                </div>
            </div>

                {orders.length === 0 ? (
                    <p>Không có đơn hàng nào.</p>
                ) : (
                    orders.map((order, index) => (
                        <div key={order.id_order} className="section_info_order">
                            <div className="info_order_left">
                                {index === 0 || orders[index - 1].id_order !== order.id_order ? (
                                    <>
                                        <h3>{order.id_order}</h3>
                                        <div className="status">
                                            <p>Ngày lập đơn: {order.day_bought}</p>
                                            <button className="btn_status">{order.status_order}</button>
                                            <button className="btn_delete" onClick={() => handleClickDeleteOrder(order.id_order)}>Hủy đơn</button>
                                        </div>
                                        <p className="total_money_order">
                                            {order.total_money} VND
                                        </p>
                                    </>
                                ) : null}
                            </div>
                            <div className="info_order_right">
                                {index === 0 || orders[index - 1].id_order !== order.id_order ? (
                                    <>
                                        <h3 className="info_introduce">Chi tiết sản phẩm đơn hàng</h3>
                                        <table className="list_products">
                                            <thead>
                                                <tr>
                                                    <th>Ảnh sản phẩm</th>
                                                    <th>Tên sản phẩm</th>
                                                    <th>Giá sản phẩm</th>
                                                    <th>Số lượng</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {orders
                                                    .filter((orderItem) => order.id_order === orderItem.id_order)
                                                    .reduce((uniqueProducts, filteredOrder) => {
                                                        if (
                                                            !uniqueProducts.some(
                                                                (product) =>
                                                                    product.name_product === filteredOrder.name_product,
                                                            )
                                                        ) {
                                                            uniqueProducts.push(filteredOrder);
                                                        }
                                                        return uniqueProducts;
                                                    }, [])
                                                    .map((uniqueProduct) => (
                                                        <tr key={uniqueProduct.id_order}>
                                                            <td>
                                                                <img
                                                                    src={uniqueProduct.image_represent}
                                                                    alt="Product"
                                                                />
                                                            </td>
                                                            <td>{uniqueProduct.name_product}</td>
                                                            <td>{uniqueProduct.price_bought}</td>
                                                            <td>{uniqueProduct.quantity}</td>
                                                        </tr>
                                                    ))}
                                            </tbody>
                                        </table>
                                    </>
                                ) : null}
                            </div>
                        </div>
                    ))
                )}
            

            <Footer />
        </>
    );
}

export default Info;
