import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { fa1, fa2, fa3, faCartShopping, faCheck, faCircle, faMoneyBill1Wave, faSpa, faTree } from '@fortawesome/free-solid-svg-icons';

function Carts({ isLoggedIn, username }) {
  const cartsAPI = 'http://localhost:3000/cart-bought';
  const [carts, setCarts] = useState([]);
  const [quantities, setQuantities] = useState({});
  const [totalMoney, setTotalMoney] = useState(0);

  useEffect(() => {
    fetch(cartsAPI)
      .then((response) => response.json())
      .then((data) => {
        setCarts(data);
        setQuantities(data.reduce((acc, cart) => ({ ...acc, [cart.id]: cart.quantity }), {}));
      });
  }, []);

  const decreaseQuantity = (id) => {
    if (quantities[id] > 1) {
      setQuantities((prevQuantities) => ({
        ...prevQuantities,
        [id]: prevQuantities[id] - 1,
      }));
    }
  };

  const increaseQuantity = (id) => {
    setQuantities((prevQuantities) => ({
      ...prevQuantities,
      [id]: (prevQuantities[id] || 0) + 1,
    }));
  };

  useEffect(() => {
    updateTotalMoney();
  }, [quantities]);

  const updateTotalMoney = () => {
    let total = 0;
    carts.forEach((cart) => {
      const quantity = quantities[cart.id] || cart.quantity;
      total += cart.price_bought * quantity;
    });
    setTotalMoney(total);
  };

  return (
    <>
      {isLoggedIn ? (
        <></>
      ) : (
        <>
          {/* <Header isLoggedIn={isLoggedIn} username={username} /> */}
        </>
      )}

      <div className="section-first-title">
        <FontAwesomeIcon icon={faTree} />
        <h3>Thông tin giỏ hàng</h3>
        <FontAwesomeIcon icon={faTree} />
        <div className="icon-mid">
          <FontAwesomeIcon icon={faSpa} />
        </div>
      </div>

      <div className="container_list_carts">
        <div className="top_infomation">
          <div className="stages_of_cart">
            <div className="stage_1">
              <FontAwesomeIcon className="phase" icon={faCartShopping} />
              <p>Giỏ hàng của tôi</p>
              <FontAwesomeIcon className="fa-light" icon={fa1} />
            </div>
            <div className="stage_2">
              <FontAwesomeIcon className="phase" icon={faMoneyBill1Wave} />
              <p>Giỏ hàng của tôi</p>
              <FontAwesomeIcon icon={fa2} />
            </div>
            <div className="stage_3">
              <FontAwesomeIcon className="phase" icon={faCheck} />
              <p>Giỏ hàng của tôi</p>
              <FontAwesomeIcon icon={fa3} />
            </div>
          </div>
        </div>
        <div className="bottom_infomation">
          <div className="section_products_cart">
            <table className="list_products">
              <thead>
                <tr>
                  <th></th>
                  <th>Tên sản phẩm</th>
                  <th>Giá cả</th>
                  <th>Số lượng</th>
                  <th>Thành tiền</th>
                </tr>
              </thead>
              <tbody>
                {carts.map((cart) => (
                  <tr key={cart.id}>
                    <td>
                      <img src={cart.image_represent} alt="" />
                    </td>
                    <td>{cart.name_product}</td>
                    <td>{cart.price_bought}</td>
                    <td>
                      <div className="quantity">
                        <button className="btn_minus" onClick={() => decreaseQuantity(cart.id)}>
                          -
                        </button>
                        <span className="quantity_value">{quantities[cart.id] || cart.quantity}</span>
                        <button className="btn_plus" onClick={() => increaseQuantity(cart.id)}>
                          +
                        </button>
                      </div>
                    </td>
                    <td className="total_money">{cart.price_bought * (quantities[cart.id] || cart.quantity)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
            <div className="total_money_cart">
              <p className="result">
                Tổng tiền: <span>{totalMoney}</span>
              </p>
              <button className="continute_buy_product">Tiếp tục mua hàng</button>
              <button className="pay">Thanh toán</button>
            </div>
          </div>
        </div>
      </div>

      {/* <Footer /> */}
    </>
  );
}

export default Carts;
