import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './register.scss';

function Register() {
    const [accounts, setAccounts] = useState([]);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleCreateAccount = () => {
        const accountAPI = 'http://localhost:3000/accounts';

        const newAccount = {
            name_clients: name,
            gender_client: gender,
            address_client: address,
            username_account: username,
            password_account: password,
        };

        fetch(accountAPI, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(newAccount),
        })
            .then((response) => response.json())
            .then((data) => {
                setAccounts([...accounts, data]);
                setUsername('');
                setPassword('');
                // navigate('/login');
            })
            .catch((error) => console.error('Error creating post:', error));
    };

    return (
        <div className="box">
            <div className="form">
                <img src="logo.jpg" alt="Logo"></img>
                <h2>Đăng ký tài khoản</h2>
                <form>
                    <div className="inputBox">
                        <input
                            type="text"
                            id="username"
                            name="username"
                            placeholder="Nhập họ và tên:"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                        />
                    </div>
                    <div className="inputBox">
                        <input
                            type="text"
                            id="username"
                            name="username"
                            placeholder="Nhập tên tài khoản"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                        />
                    </div>
                    <select>
                        <option>option01</option>
                        <option>option02</option>
                        <option>option03</option>
                        <option>option04</option>
                        <option>option05</option>
                        <option>option06</option>
                        <option>option07</option>
                        <option>option08</option>
                        <option>option09</option>
                        <option>option10</option>
                    </select>
                    <div className="inputBox">
                        <input
                            type="password"
                            name="password"
                            placeholder="Nhập mật khẩu tài khoản"
                            id="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
                    </div>

                    <button className="btn-logins" type="submit" onClick={handleCreateAccount}>
                        Create
                    </button>
                </form>
                <button className="btn-dangky">
                    <a href="http://localhost:2112/login">Đăng nhập</a>
                </button>
            </div>
        </div>
    );
}

export default Register;
