import React, { useState, useEffect } from "react";
import { Link, useNavigate } from 'react-router-dom';
import './register.scss'

function Register(){

    const handleSubmit = (e) => {
        e.preventDefault();
        if (username === 'itachi' && password === '123456') {
          onLogin(username);
          history('/')
        } else {
          alert('Thông tin đăng nhập không chính xác');
        }
      };

    return (
        <>
            <div class="box">
        <div className="form">
          
          <img src="logo.jpg"></img>
          <h2>Đăng ký tài khoản</h2>
          <form onSubmit={handleSubmit}>
          <div class="inputBox">
            <input
              type="text"
              id="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>
          <div class="inputBox">
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <button className="btn-logins" type="submit">Đăng nhập</button>
          </form>
          <button className="btn-dangky"><a>dăng ký</a></button>
        </div>
        
      </div>
        </>
    )
}

export default Register;
