import React, { useState, useEffect } from 'react';
// import './header.scss';
import { menuData } from '../../data-plants';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircleXmark, faMagnifyingGlass, faSpinner, faCartShopping } from '@fortawesome/free-solid-svg-icons';
import { useLocation, useNavigate } from 'react-router-dom';
import Header from '../header/Header';

const Introduce = ({ isLoggedIn, username, id, onLogout }) => {
    return(
        <>
            {/* <Header /> */}

            <div className="container">
                <img src="background.jpg"></img>
                <div className="section_introduce_first">
                    <h3>Chào mừng bạn đến với trang web "Cây Cảnh Việt"!</h3>
                    <p>
                    Chúng tôi tự hào là địa chỉ đáng tin cậy cho những người yêu thích và đam mê cây cảnh tại Việt Nam. Trang web của chúng tôi mang đến cho bạn một thế giới đa dạng và phong phú về cây cảnh, từ những loại cây trang nhã và sang trọng cho đến các loại cây dễ chăm sóc, phù hợp với mọi không gian.
                    </p>
                    <img src="background.jpg"></img>
                </div>
                <div className="section_introduce_second">
                    <h3>Những sản phẩm mà cá cảnh Sài Gòn đang kinh doanh</h3>
                    <p>
                    Hiện tại, đa phần chúng tôi sẽ chuyên kinh doanh các loại cá cảnh hiện đang có trên thị trường TPHCM để đáp ứng nhu cầu của bất kỳ ai và luôn đảm bảo đẹp và đủ kiểu để mọi người lựa chọn.
Đồng thời, không chỉ có cá cảnh thôi mà Cá Cảnh Sài Gòn còn cung cấp các dòng sản phẩm để hỗ trợ người mua như: Hồ cá cảnh, tiểu cảnh hồ cá, bể cá nhà hàng và các loại thức ăn cho cá….
Tất cả những sản phẩm này đều được đảm bảo về chất lượng lẫn kiểu mẫu, riêng thức ăn cho cá thì luôn đa dạng và phù hợp với từng loại cá mà khách hàng cần mua.
                    </p>
                    <img src="background.jpg"></img>
                </div>
                <div className="section_introduce_thrid">
                    <h3>Mục tiêu phát triển của của hàng cây cảnh</h3>
                </div>

            </div>
            
        </>
    )
};

export default Introduce;
