import { faCircleXmark, faMagnifyingGlass, faSpinner, faCartShopping } from '@fortawesome/free-solid-svg-icons';
import { useLocation, useNavigate } from 'react-router-dom';
import Header from '../header/Header';

const Introduce = ({ isLoggedIn, username, id, onLogout }) => {
    return(
        <>
            {/* <Header /> */}

            <div className="container">
                <img src="background.jpg"></img>
                <div className="section_introduce_first">
                    <h3>Chào mừng bạn đến với trang web "Cây Cảnh Việt"!</h3>
                </div>
            </div>
            
        </>
    )
};

export default Introduce;
