import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

function Map() {
  const saigonPosition = [10.8231, 106.6297]; // Vị trí Sài Gòn (latitude và longitude)

  const customIcon = new L.Icon({
    iconUrl: 'your-icon-url.png', // Thay thế bằng URL hình ảnh biểu tượng của bạn (nếu muốn)
    iconSize: [32, 32],
    iconAnchor: [16, 32],
    popupAnchor: [0, -32],
  });

  return (
    <MapContainer center={saigonPosition} zoom={12} style={{ height: '400px', width: '100%' }}>
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      />
      <Marker position={saigonPosition} icon={customIcon}>
        <Popup>Sài Gòn</Popup>
      </Marker>
    </MapContainer>
  );
}

export default Map;
