import React, { useState, useEffect } from "react";
import { Link, useNavigate } from 'react-router-dom';
import './register.scss'

function Register(){
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleCreateAccount = (e) => {
    e.preventDefault();

    const accountAPI = 'http://localhost:3000/accounts';

    const newAccount = {
      user_account: username,
      password_account: password
    };

    fetch(accountAPI, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(newAccount)
    })
      .then(response => response.json())
      .then(data => {
        setUsername('');
        setPassword('');
        navigate('http://localhost:2112/register'); // Chuyển hướng đến trang đăng nhập sau khi tạo tài khoản thành công
      })
      .catch(error => console.error('Error creating account:', error));
    }
    return (
      <div className="box">
        <div className="form">
          <img src="logo.jpg" alt="Logo"></img>
          <h2>Đăng ký tài khoản</h2>
          <form onSubmit={handleCreateAccount}>
            <div className="inputBox">
              <input
                type="text"
                id="username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
            </div>
            <div className="inputBox">
              <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            <button className="btn-logins" type="submit">Đăng ký</button>
          </form>
        </div>
      </div>
    );
}

export default Register;
