import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './register.scss';

function Register() {
    const [accounts, setAccounts] = useState([]);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [name_client, setNameClient] = useState('');
    const [gender, setGenderClient] = useState('');
    const [address, setAddressClient] = useState('');
    const [email, setEmailClient] = useState('');
    const [phone, setPhoneClient] = useState('');
    const [avatar, setAvatar] = useState(null);
    const navigate = useNavigate();

    const [carts, setCarts] = useState([]);

    // function generateRandomCode() {
    //     const prefix = 'AC';
    //     const stt = Math.floor(Math.random() * 100); // Số thứ tự ngẫu nhiên từ 0 đến 999
    //     const code = `${prefix}${stt.toString().padStart(2, '0')}`; // Ghép chữ cái "AC" với STT, STT có độ dài 3 chữ số

    //     return code;
    // }

    //tạo account
    const handleCreateAccount = () => {
        const accountAPI = 'http://localhost:3000/accounts';
        const newAccount = {
            name_clients: name_client,
            email_client: email,
            phone_client: phone,
            address_client: address,
            username_account: username,
            password_account: password,
            avatar_account: avatar
        };

        fetch(accountAPI, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(newAccount),
        })
            .then((response) => response.json())
            .then((data) => {
                setAccounts([...accounts, data]);
                setUsername('');
                setPassword('');
                // navigate('/login');
            })
            .catch((error) => console.error('Error creating post:', error));
    };

    const handleAvatarChange = (e) => {
        const file = e.target.files[0];
        // Kiểm tra nếu người dùng đã chọn hình ảnh
        if (file) {
            setAvatar(file);
        }
    };

    return (
        <div className="box_register">
            <div className="form">
                <img src="logo.jpg" alt="Logo"></img>
                <h2>Đăng ký tài khoản</h2>
                <form>
                    <div>
                        
                    </div>

                    <div clasName="info_account">
                    <div className="inputBox">
                        <input
                            type="text"
                            id="username"
                            name="username"
                            placeholder="Nhập tên tài khoản"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                        />
                    </div>

                    <div className="inputBox">
                        <input
                            type="password"
                            name="password"
                            placeholder="Nhập mật khẩu tài khoản"
                            id="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
                    </div>

                    <div className="inputBox">
                        <input type="file" name="avatar" accept="image/*" onChange={handleAvatarChange} />
                    </div>
                    </div>

                    <button className="btn-logins" type="submit" onClick={handleCreateAccount}>
                        Create
                    </button>
                </form>
                <button className="btn-dangky">
                    <a href="http://localhost:2112/login">Đăng nhập</a>
                </button>
            </div>
        </div>
    );
}

export default Register;
