import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

function Login() {
    const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    //   const history = useHistory();/

    useEffect(() => {
        // Kiểm tra nếu đã đăng nhập từ trước
        const storedLoginStatus = localStorage.getItem('isLoggedIn');
        if (storedLoginStatus === 'true') {
            setIsLoggedIn(true);
        }
    }, []);

    const handleLogin = () => {
        // Thực hiện xác thực đăng nhập và cập nhật trạng thái isLoggedIn

        setIsLoggedIn(true);

        // Lưu trạng thái đăng nhập vào Local Storage
        localStorage.setItem('isLoggedIn', 'true');

        // Chuyển hướng người dùng về trang chủ
        // window.location.href = '/';
        const username = "example_user";
        window.location.href = `http://localhost:3000?username=${username}`;
    };

    const handleLogout = () => {
        // Xử lý đăng xuất
        setIsLoggedIn(false);

        // Xóa trạng thái đăng nhập khỏi Local Storage
        localStorage.removeItem('isLoggedIn');
    };

    return (
        <>
            {isLoggedIn ? (
                <>
                    <p>Bạn đã đăng nhập thành công!</p>
                    <button className="btn-header btn-login" onClick={handleLogout}>
                        Đăng xuất
                    </button>
                </>
            ) : (
                <button className="btn-header btn-login" onClick={handleLogin}>
                    Đăng nhập
                </button>
            )}
            <button className="btn-header btn-sub">Đăng ký</button>
        </>
    );
}

export default Login;
