import './body.scss';
import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
    faChildReaching,
    faCircleXmark,
    faHeart,
    faLeaf,
    faMagnifyingGlass,
    faMoneyBillTrendUp,
    faSpinner,
    faArrowRight,
    faArrowLeft,
} from '@fortawesome/free-solid-svg-icons';

const Body = () => {
    const plantsAPI = 'http://localhost:3000/plants-home';

    const [plants, setPlants] = useState([]);

    useEffect(() => {
        fetch(plantsAPI)
            .then((response) => response.json())
            .then((data) => setPlants(data));
    }, []);
    const limitedPlants = plants.slice(0, 4); // Giới hạn chỉ lấy 4 phần tử đầu tiên
    // if (plants.length === 0) {
    //   return <div>Loading...</div>;
    // }

    const next = document.querySelector('.left-button');
    const prev = document.querySelector('.right-button');
    const comment = document.querySelector('.detail-plant');
    // var translateX = 0;
    // var count = limitedPlants.length

    const [translateX, setTranslateX] = useState(0);
    const [count, setCount] = useState(0);

    const handleNext = () => {
        // if (count < plants.length - 4) {
        setTranslateX(translateX - 250);
        setCount(count + 1);
        // }
    };

    const handlePrev = () => {
        // if (count > 0) {
        setTranslateX(translateX + 250);
        setCount(count - 1);
        // }
    };

    return (
        <>
            <div className="body_first">
                <img src="background.jpg" alt="" />
                <div className="benefit">
                    <h2>Lợi ích mua cây</h2>
                    <div className="list-benefit">
                        <div className="item-benefit">
                            <FontAwesomeIcon className="icons" icon={faHeart} />
                            <div className="content">
                                <h3>Tạo cảm giác thoải mái</h3>
                                <p>
