import { useEffect, useState } from 'react';
import classNames from 'classnames/bind';
import styles from './Header.module.scss';
import images from '~/assets/images';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircleQuestion, faCircleXmark, faCloudUpload, faCoins, faEarthAsia, faEllipsisVertical, faGear, faKeyboard, faMagnifyingGlass, faMessage, faSignIn, faSignOut, faSpinner, faUser } from '@fortawesome/free-solid-svg-icons';
import { Wrapper as PopperWrapper } from '~/components/Popper';

import HeadlessTippy from '@tippyjs/react/headless';
import Tippy from '@tippyjs/react';
import AccountItem from '~/components/AccountItem';
import Button from '~/components/Button';
import Menu from '~/components/Popper/Menu';
import 'tippy.js/dist/tippy.css';
import { UploadIcon } from '~/components/Icons';

//import 'tippy.js/dist/tippy.css';

const cx = classNames.bind(styles);
const MENU_ITEMS = [
    {
    icon: <FontAwesomeIcon icon = {faEarthAsia} />,
    title: 'English',
    children:{
        title: 'Language',
        data:[
            {
                code: 'en',
                title: 'English',
            },
            {
                code: 'vi',
                title: 'Tieng Viet',
            }
        ],
    }
    },
    {
        icon: <FontAwesomeIcon icon = {faCircleQuestion} />,
        title: 'Feedback and help',
        to: 'feedback'
    },
    {
        icon: <FontAwesomeIcon icon = {faKeyboard} />,
        title: 'Keyboard shortcuts',
    }
]

    const userMenu = [
        {
            icon: <FontAwesomeIcon icon = {faUser} />,
            title: 'View profile',
            to: '/@holyy',
        },
        {
            icon: <FontAwesomeIcon icon = {faCoins} />,
            title: 'Get coins',
            to: '/coin',
        },
        {
            icon: <FontAwesomeIcon icon = {faGear} />,
            title: 'Settings',
            to: '/settings',
        },
        ...MENU_ITEMS,
        {
            icon: <FontAwesomeIcon icon = {faSignOut} />,
            title: 'Log out',
            to: '/logout',
            separate: true,
        },
    ]

    const currentUser = true;

function Header() {
    const [searchResult, setSearchResult] = useState([]);

    useEffect(() =>{
        setTimeout(()=>{
            setSearchResult([])
        }, 0);
    })

    const handleMenuChange = (menuItem) =>{
        switch (menuItem.type){
            case 'language':
                break;
            default:
        }
    }

    

    return (
        <header className={cx('wrapper')}>
            <div className={cx('inner')}>
                <div className={cx('logo')}>
                    <img src={images.logo} alt="TikTok" />
                </div>
                <HeadlessTippy 
                    interactive
                    visible={searchResult.length > 0}
                    render={attrs => (
                        
                            <div className={cx('search-result')} tabIndex="-1" {...attrs}>
                               <PopperWrapper>
                                    <h4 className={cx('search-title')}>Accounts</h4>
                                    <AccountItem />
                                    <AccountItem />
                                    <AccountItem />

                               </PopperWrapper>
                            </div>
                        
                    )}
                >
                <div className={cx('search')}>
                    <input placeholder="Search accounts and video" spellCheck={false} />
                    <button className={cx('clear')}>
                        <FontAwesomeIcon icon={faCircleXmark} />
                    </button>
                    <FontAwesomeIcon className={cx('loading')} icon={faSpinner} />
                        <button className={cx('search-btn')}>
                            <FontAwesomeIcon icon={faMagnifyingGlass} />
                        </button>
                </div>
                </HeadlessTippy>

                <div className={cx('actions')}>
                    {currentUser ? (
                        <>
                            <div className={cx('current-user')}>
                                <Tippy delay={[0, 200]} content="Upload video" placement='bottom'>
                                    <button className={cx('action-btn')}>
                                        {/* <FontAwesomeIcon icon={faCloudUpload} /> */}
                                        {/* <UploadIcon /> */}
                                    </button>
                                    
                                </Tippy>

                            </div>
                        </>
                    ) : (
                        <>
                            <Button text>Update</Button>
                            <Button primary>Log in</Button>             
                        </>                
                    )}

                    <Menu items={currentUser ? userMenu : MENU_ITEMS} onChange={handleMenuChange}>
                        {currentUser ? (
                            <img
                                src="https://saigonbling.b-cdn.net/wp-content/uploads/2022/07/ho-ly-la-gi-2.jpg"
                                className={cx('user-avatar')}
                                alt="Nhan Hanh"
                            />
                        ):(
                            <button className={cx('more-btn')}>
                                <FontAwesomeIcon icon={faEllipsisVertical} />
                            </button>
                        )}
                        
                    </Menu>

                </div>
                
            </div>
        </header>
    );
}
export default Header;
