import { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './login.scss';
function Login({ onLogin }) {
    const [accounts, setAccounts] = useState([]);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const history = useNavigate();

// lấy dữ liệu
const handleAccount = () => {
    const accountAPI = 'http://localhost:3000/accounts';

    fetch(accountAPI)
      .then(response => response.json())
      .then(data => {setAccounts(data);
      handleSubmit();
      }
      );
  };
//   kết thúc lấy data

    const handleSubmit = (e) => {
        e.preventDefault();
        const account = accounts.find(account => account.username_account === username);
        if (username === account && password === '123456') {
            onLogin(username);
            history('/');
        } else {
            alert('Thông tin đăng nhập không chính xác');
        }
    };

    return (
        <>
            <div class="box">
                <div className="form">
                    <img src="logo.jpg"></img>
                    <h2>Đăng nhập</h2>
                    <form onSubmit={handleSubmit}>
                        <div class="inputBox">
                            <input
                                type="text"
                                id="username"
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                            />
                        </div>
                        <div class="inputBox">
                            <input
                                type="password"
                                id="password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                            />
                        </div>
                        <button className="btn-logins" type="submit">
                            Đăng nhập
                        </button>
                    </form>
                    <button className="btn-dangky">
                        <a href="http://localhost:2112/register">Đăng ký</a>
                    </button>
                </div>
            </div>
        </>
    );
}

export default Login;
