import React, { useState } from 'react';
import Header from '~/pages/header/Header';

function updateCarts() {
  const [buttonClicked, setButtonClicked] = useState(false);

  const handleButtonClick = () => {
    // Xử lý khi ấn nút
    // Đặt giá trị của buttonClicked thành true
    setButtonClicked(true);
  };

  return (
    <div>
      {/* Đặt nút và gọi hàm handleButtonClick khi ấn */}
      <button onClick={handleButtonClick}>Ấn vào đây</button>
      {/* Truyền state buttonClicked xuống cho Header */}
      <Header buttonClicked={buttonClicked} />
      {/* Các phần nội dung khác của file updateCarts.js */}
    </div>
  );
}

export default updateCarts;
