import './body.scss';
import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { Link } from 'react-router-dom';
import {
    faChildReaching,
    faCircleXmark,
    faHeart,
    faLeaf,
    faMagnifyingGlass,
    faMoneyBillTrendUp,
    faSpinner,
    faArrowRight,
    faArrowLeft,
} from '@fortawesome/free-solid-svg-icons';


const Body = ({ username, id }) => {

    const plantsAPI_1 = "http://localhost:3000/plants_home?type_plant=Cay_De_Ban";
    const plantsAPI_2 = "http://localhost:3000/plants_home?type_plant=Cay_Mini";
    const plantsAPI_3 = "http://localhost:3000/plants_home?type_plant=Cay_Noi_Thạt";
    const plantsAPI_4 = "http://localhost:3000/plants_home?type_plant=Cay_De_Ban";
    // chứa thông tin danh sách hàng
    const [plants, setPlants] = useState([]);
    //lấy data truyền vào plants
    useEffect(() => {
        fetch(plantsAPI)
            .then((response) => response.json())
            .then((data) => setPlants(data));
    }, []);

    const comment = document.querySelector('.detail-plant');

    const [translateX1, setTranslateX1] = useState(0);
    const [count1, setCount1] = useState(0);

    const [translateX2, setTranslateX2] = useState(0);
    const [count2, setCount2] = useState(0);
    // xử lý di chuyển chọn lựa sản phẩm sang phải
    const handleNext1 = () => {
        if (count1 === plants.length - 4) {
            setCount1(count1);
        } else {
            setTranslateX1(translateX1 - 300);
            setCount1(count1 + 1);
        }
    };

    // xử lý di chuyển chọn lựa sản phẩm sang trái
    const handlePrev1 = () => {
        if (count1 === 0) {
            setTranslateX1(translateX1 + 0);
        } else {
            setTranslateX1(translateX1 + 300);
            setCount1(count1 - 1);
        }
    };

    // xử lý di chuyển chọn lựa sản phẩm sang phải
    const handleNext2 = () => {
        if (count2 === plants.length - 4) {
            setCount2(count2);
        } else {
            setTranslateX2(translateX2 - 300);
            setCount2(count2 + 1);
        }
    };

    // xử lý di chuyển chọn lựa sản phẩm sang trái
    const handlePrev2 = () => {
        if (count2 === 0) {
            setTranslateX2(translateX2 + 0);
        } else {
            setTranslateX2(translateX2 + 300);
            setCount2(count2 - 1);
        }
    };

    //thêm sản phẩm vào giỏ hàng
    const handleClickAddToCart = (plant) => {
        handleAddToCart(plant);
    };
    const idString = id.toString();
    //thêm sản phẩm vào giỏ hàng
    const handleAddToCart = (plantId) => {
        const CartssAPI = 'http://localhost:3000/cart_bought';
        const newCart = {
            owner: idString,
            id_plant: plantId.id,
            image_represent: plantId.picture_main,
            name_product: plantId.name,
            price_bought: plantId.price_new,
            quantity: 1,
        };

        // Gửi request GET đến API để lấy dữ liệu hiện tại
        fetch(CartssAPI)
            .then((response) => response.json())
            .then((data) => {
                // Kiểm tra xem sản phẩm đã tồn tại trong giỏ hàng chưa
                const existingCartItem = data.find((item) => item.owner === idString && item.id_plant === plantId.id);

                if (existingCartItem) {
                    // Nếu sản phẩm đã tồn tại, tăng số lượng lên +1
                    const updatedCart = {
                        ...existingCartItem,
                        quantity: existingCartItem.quantity + 1,
                    };

                    // Gửi request PUT đến API để cập nhật thông tin sản phẩm trong giỏ hàng
                    fetch(`${CartssAPI}/${existingCartItem.id}`, {
                        method: 'PUT',
                        headers: {
                            'Content-Type': 'application/json',
                        },
                        body: JSON.stringify(updatedCart),
                    })
                        .then((response) => response.json())
                        .then((data) => {})
                        .catch((error) => console.error('Error updating cart item:', error));
                } else {
                    // Nếu sản phẩm chưa tồn tại, thêm sản phẩm mới vào giỏ hàng
                    fetch(CartssAPI, {
                        method: 'POST',
                        headers: {
                            'Content-Type': 'application/json',
                        },
                        body: JSON.stringify(newCart),
                    })
                        .then((response) => response.json())
                        .then((data) => {})
                        .catch((error) => console.error('Error fetching carts:', error));
                }
            })
            .catch((error) => console.error('Error fetching carts:', error));

    };

    return (
        <>
            <div className="body_first">
                <img src="background.jpg" alt="" />
                <div className="benefit">
                    <h2>Lợi ích mua cây</h2>
                    <div className="list-benefit">
                        <div className="item-benefit">
