import Header from '~/pages/header/Header';
import Footer from '~/components/footer/Footer';

import React, { useState, useEffect } from 'react';
import './carts.scss';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
    fa1,
    fa2,
    fa3,
    faCartShopping,
    faCheck,
    faCircle,
    faMoneyBill1Wave,
    faSpa,
    faStar,
    faTree,
} from '@fortawesome/free-solid-svg-icons';

function Carts({ isLoggedIn, username }) {
    // khai báo api lấy các sản phẩm trong giỏ hàng
    const cartsAPI = 'http://localhost:3000/cart-bought';
    const [carts, setCarts] = useState([]);
    // tạo kết nối
    useEffect(() => {
        fetch(cartsAPI)
            .then((response) => response.json())
            .then((data) => setCarts(data));
    }, [carts.length]);

    const [quantities, setQuantities] = useState([1, 1]);
    const [totalMoney, setTotalMoney] = useState([0, 0]);

    // const decreaseQuantity = (index) => {
    //     const newQuantities = [...quantities];
    //     if (newQuantities[index] > 1) {
    //         newQuantities[index] -= 1;
    //         setQuantities(newQuantities);
    //     }
    // };

    // const increaseQuantity = (index) => {
    //     const newQuantities = [...quantities];
    //     newQuantities[index] += 1;
    //     setQuantities(newQuantities);
    // };

    const decreaseQuantity = (id) => {
        if (quantities[id] > 1) {
            setQuantities((prevQuantities) => ({
                ...prevQuantities,
                [id]: prevQuantities[id] - 1,
            }));
        }
    };

    const increaseQuantity = (id) => {
        setQuantities((prevQuantities) => ({
            ...prevQuantities,
            [id]: prevQuantities[id] ? prevQuantities[id] + 1 : 1,
        }));
    };

    return (
        <>
            {isLoggedIn ? (
                <></>
            ) : (
                <>
                    <Header isLoggedIn={isLoggedIn} username={username} />
                </>
            )}

            <div className="section-first-title">
                <FontAwesomeIcon icon={faTree} />
                <h3>Thông tin giỏ hàng</h3>
                <FontAwesomeIcon icon={faTree} />
                <div className="icon-mid">
                    <FontAwesomeIcon icon={faSpa} />
                </div>
            </div>

            <div class="container_list_carts">
                <div class="top_infomation">
                    <div class="stages_of_cart">
                        <div class="stage_1">
                            <FontAwesomeIcon className="phase" icon={faCartShopping} />
                            <p>Giỏ hàng của tôi</p>
                            <FontAwesomeIcon className="fa-light" icon={fa1} />
                        </div>
                        <div class="stage_2">
                            <FontAwesomeIcon className="phase" icon={faMoneyBill1Wave} />
                            <p>Giỏ hàng của tôi</p>
                            <FontAwesomeIcon icon={fa2} />
                        </div>
                        <div class="stage_3">
                            <FontAwesomeIcon className="phase" icon={faCheck} />
                            <p>Giỏ hàng của tôi</p>
                            <FontAwesomeIcon icon={fa3} />
                        </div>
                    </div>
                </div>
                <div class="bottom_infomation">
                    <div class="section_products_cart">
                        <table class="list_products">
                            <tr>
                                <th></th>
                                <th>Tên sản phẩm</th>
                                <th>Giá cả</th>
                                <th>Số lượng</th>
                                <th>Thành tiền</th>
                            </tr>
                            {carts.map((cart) => (
                                <>
                                    <tr>
                                        <td>
                                            <img src={cart.image_represent} />
                                        </td>
                                        <td>{cart.name_product}</td>
                                        <td>{cart.price_bought}</td>
                                        <td>
                                            <div className="quantity">
                                                <button className="btn_minus" onClick={() => decreaseQuantity(cart.id)}>
                                                    -
                                                </button>

                                                <span className="quantity_value">{quantities[cart.id] || 1}</span>
                                                <button className="btn_plus" onClick={() => increaseQuantity(cart.id)}>
                                                    +
                                                </button>
                                            </div>
                                        </td>
                                        <td className="total_money">{totalMoney[0]}</td>
                                    </tr>
                                </>
                            ))}
                        </table>
                        <div className="total_money_cart">
                            <p className="result">
                                Tổng tiền: <span>20000</span>
                            </p>
                            <button className="continute_buy_product">Tiếp tục mua hàng</button>
                            <button className="pay">Thanh toán</button>
                        </div>
                    </div>
                </div>
            </div>

            {/* <Footer /> */}
        </>
    );
}

export default Carts;
