import './body.scss';
import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { Link } from 'react-router-dom';
import {
    faChildReaching,
    faCircleXmark,
    faHeart,
    faLeaf,
    faMagnifyingGlass,
    faMoneyBillTrendUp,
    faSpinner,
    faArrowRight,
    faArrowLeft,
} from '@fortawesome/free-solid-svg-icons';

const Body = (username) => {
    // test
    const accountAPI = 'http://localhost:3000/accounts';
    let id = null;
    fetch(accountAPI)
        .then((response) => response.json())
        .then((accounts) => {
            const account = accounts.find((acc) => acc.username_account === username);
            if (account) {
                id = account.id;
            }
        });

    const plantsAPI = 'http://localhost:3000/plants_home';
    // gọi mảng chứa hàng hóa
    const [carts, setCarts] = useState([]);

    const [plants, setPlants] = useState([]);

    useEffect(() => {
        fetch(plantsAPI)
            .then((response) => response.json())
            .then((data) => setPlants(data));
    }, []);
    const limitedPlants = plants.slice(0, 4); // Giới hạn chỉ lấy 4 phần tử đầu tiên
    // if (plants.length === 0) {
    //   return <div>Loading...</div>;
    // }

    const comment = document.querySelector('.detail-plant');
    // var translateX = 0;
    // var count = limitedPlants.length

    const [translateX, setTranslateX] = useState(0);
    const [count, setCount] = useState(0);

    const handleNext = () => {
        if (count === plants.length - 4) {
            setCount(count);
        } else {
            setTranslateX(translateX - 300);
            setCount(count + 1);
        }
    };

    const handlePrev = () => {
        if (count === 0) {
            // alert(translateX);
            setTranslateX(translateX + 0);
            // setCount(count - 1);
        } else {
            // alert(translateX);
            setTranslateX(translateX + 300);
            setCount(count - 1);
        }
    };
    // const cartssAPI = ``;
    // thêm sản phẩm vào giỏ hàng
    // const handleAddToCart = (plantId) => {
    //     const CartssAPI = 'http://localhost:3000/cart_bought';

    //     // Tạo object newCart từ plantId
    //     const newCart = {
    //       id: plantId.id,
    //       image_represent: plantId.pic,
    //       name_product: plantId.name,
    //       price_bought: plantId.price_new,
    //       quantity: 1,
    //     };

    // Gửi request POST đến API để thêm newCart vào carts
    // fetch(CartssAPI, {
    //   method: 'POST',
    //   headers: {
    //     'Content-Type': 'application/json',
    //   },
    //   body: JSON.stringify({ AC70: { carts: [newCart] } }), // Chỉnh sửa body request để tạo structure với AC70 và mảng carts
    // })
    //   .then((response) => response.json())
    //   .then((data) => {
    //     console.log(data); // In ra dữ liệu trả về từ API sau khi thêm newCart vào carts
    //   })
    //   .catch((error) => console.error('Error fetching carts:', error));

    // };

    const handleAddToCart = (plantId) => {
        const CartssAPI = 'http://localhost:3000/cart_bought';
        // Tạo object newCart từ plantId
        const newCart = {
            owner: use,
            id: plantId.id,
            image_represent: plantId.pic,
            name_product: plantId.name,
            price_bought: plantId.price_new,
            quantity: 1,
        };
        //Gửi request GET đến API để lấy dữ liệu hiện tại
        fetch(CartssAPI, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(newCart), // Chỉnh sửa body request để tạo structure với AC70 và mảng carts
        })
            .then((response) => response.json())
            .then((data) => {
                // console.log(data); // In ra dữ liệu trả về từ API sau khi thêm newCart vào carts
            })
            .catch((error) => console.error('Error fetching carts:', error));
    };

    const handleClickAddToCart = (plant) => {
        handleAddToCart(plant);
    };

    return (
        <>
            <div className="body_first">
                <img src="background.jpg" alt="" />
                <div className="benefit">
                    <h2>Lợi ích mua cây</h2>
                    <div className="list-benefit">
                        <div className="item-benefit">
                            <FontAwesomeIcon className="icons" icon={faHeart} />
                            <div className="content">
                                <h3>Tạo cảm giác thoải mái</h3>
                                <p>
                                    Chăm sóc cây trồng giúp bạn thư giãn. Từ đó, sẽ giảm thiểu được những sự mệt mỏi về
                                    tinh thần, hưng phấn hơn,vui vẻ hơn.
                                </p>
                            </div>
                        </div>
                        <div className="item-benefit">
                            <FontAwesomeIcon className="icons" icon={faChildReaching} />
                            <div className="content">
                                <h3>Sức khỏe</h3>
                                <p>
                                    Có thêm cây xanh trong nhà, góp phần không nhỏ trong việc bảo vệ sức khỏe mọi người
                                    trong nhà.
                                </p>
                            </div>
                        </div>
                        <div className="item-benefit">
                            <FontAwesomeIcon className="icons" icon={faMoneyBillTrendUp} />
                            <div className="content">
                                <h3>Thu hút tài lộc</h3>
                                <p>
                                    Ngoài việc làm đẹp cho ngôi nhà của bạn, cây cảnh còn có ý nghĩa rất lớn về mặt
                                    phong thủy.
                                </p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <div className="best-seller">
                <div className="title">
                    <FontAwesomeIcon className="icon-best-seller" icon={faLeaf} />
                    <h4>SẢN PHẨM BÁN CHẠY</h4>
                    <FontAwesomeIcon className="icon-best-seller" icon={faLeaf} />
                </div>
                <div className="product">
                    <div className="contetn-product">
                        <img src="sen-da-mini.jpg" alt="" />
                        <div className="content">
                            <h3>Tên sản phẩm</h3>
                            <h3>Giá sản phẩm</h3>
                        </div>
                    </div>
                    <div className="contetn-product">
                        <img src="sen-da-mini.jpg" alt="" />
                        <div className="content">
                            <h3>Tên sản phẩm</h3>
                            <h3>Giá sản phẩm</h3>
                        </div>
                    </div>
                    <div className="contetn-product">
                        <img src="sen-da-mini.jpg" alt="" />
                        <div className="content">
                            <h3>Tên sản phẩm</h3>
                            <h3>Giá sản phẩm</h3>
                        </div>
                    </div>
                    <div className="contetn-product">
                        <img src="sen-da-mini.jpg" alt="" />
                        <div className="content">
                            <h3>Tên sản phẩm</h3>
                            <h3>Giá sản phẩm</h3>
                        </div>
                    </div>
                    <div className="contetn-product">
                        <img src="sen-da-mini.jpg" alt="" />
                        <div className="content">
                            <h3>Tên sản phẩm</h3>
                            <h3>Giá sản phẩm</h3>
                        </div>
                    </div>
                    <div className="contetn-product">
                        <img src="sen-da-mini.jpg" alt="" />
                        <div className="content">
                            <h3>Tên sản phẩm</h3>
                            <h3>Giá sản phẩm</h3>
                        </div>
                    </div>
                </div>
            </div>

            <div className="category-plants">
                <div className="stemp">
                    <span>Yêu thích</span> <a href="#"> | xem tất cả</a>
                </div>
                <div className="list-plants">
                    <button className="arrow-button left-button" onClick={handlePrev}>
                        <FontAwesomeIcon className="icons-left" icon={faArrowLeft} />
                    </button>
                    <div className="detail-plant-container" style={{ transform: `translateX(${translateX}px)` }}>
                        {plants.map((plant) => (
                            <div key={plant.id} className="detail-plant">
                                <img src={plant.pic} alt="" />
                                <h3>{plant.name}</h3>
                                <div className="price">
                                    <div className="price-old">{plant.price_old}</div>
                                    <div className="price-new">{plant.price_new}</div>
                                </div>
                            </div>
                        ))}
                    </div>
                    <button className="arrow-button right-button" onClick={handleNext}>
                        <FontAwesomeIcon className="icons-right" icon={faArrowRight} />
                    </button>
                </div>
            </div>

            <div className="category-plants">
                <div className="stemp">
                    <span>Yêu thích</span> <a href="#"> | xem tất cả</a>
                </div>
                <div className="list-plants">
                    <button className="arrow-button left-button" onClick={handlePrev}>
                        <FontAwesomeIcon className="icons-left" icon={faArrowLeft} />
                    </button>
                    <div className="detail-plant-container" style={{ transform: `translateX(${translateX}px)` }}>
                        {plants.map((plant) => (
                            <div key={plant.id} className="detail-plant">
                                <img src={plant.pic} alt="" />
                                <h3>
                                    <a href={`http://localhost:2112/contact?id=${plant.id}`}>{plant.name}</a>
                                </h3>
                                <div className="price">
                                    <div className="price-old">{plant.price_old}</div>
                                    <div className="price-new">{plant.price_new}</div>
                                </div>
                                <button class="add-product" onClick={() => handleClickAddToCart(plant)}>
                                    CHO VÀO GIỎ HÀNG
                                </button>
                            </div>
                        ))}
                    </div>
                    <button className="arrow-button right-button" onClick={handleNext}>
                        <FontAwesomeIcon className="icons-right" icon={faArrowRight} />
                    </button>
                </div>
            </div>
        </>
    );
};

export default Body;
