import React, { useState, useEffect } from 'react';
import './header.scss';
import { menuData } from '../../data-plants';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircleXmark, faMagnifyingGlass, faSpinner, faCartShopping } from '@fortawesome/free-solid-svg-icons';
import { useLocation, useNavigate } from 'react-router-dom';

const Header = ({ isLoggedIn, username, onLogout }) => {
    // tạo mảng lưu sản phẩm trong giỏ hàng
    const [carts, setCarts] = useState([]);
    const cartsAPI = 'http://localhost:3000/cart-bought';
    //tạo kết nối giỏ hàng khi có thay đổi sẽ cập nhật
    useEffect(() => {
        fetch(cartsAPI)
            .then((response) => response.json())
            .then((data) => setCarts(data));
    },);

    // const location = useLocation();
    const [menus, setMenus] = useState(menuData);
    const history = useNavigate();
    const handleSubmit = (e) => {
        e.preventDefault();
        onLogout();
        history('/');
    };

    return (
        <>
            <header>
                <div className="header-first">
                    <ul className="main-menu">
                        {menus.map((menu) => {
                            return (
                                <>
                                    <li>
                                        <a href={menu.path}>{menu.name}</a>
                                    </li>
                                </>
                            );
                        })}
                    </ul>
                    <div className="check-account">
                        {isLoggedIn ? (
                            <>
                                <div className="cart">
                                    <FontAwesomeIcon className="cart-icon" icon={faCartShopping} />
                                    <span class="header__cart-notice">
                                        {carts.length}
                                    </span>
                                    <div className="cart-list">
                                        <h4 class="cart-heading">Sản phẩm đã thêm</h4>
                                        <ul class="cart-list-item">
                                            {carts.map((cart) => (
                                                <>
                                                    <li class="cart-item">
                                                        <img className="cart-img" src={cart.image_represent}></img>
                                                        <div class="cart-item-info">
                                                            <div class="cart-item-head">
                                                                <h5 class="cart-item-name">{cart.name_product}</h5>
                                                                <div class="cart-item-price-wrap">
                                                                    <span class="cart-item-price">
                                                                        {cart.price_bought}
                                                                    </span>
                                                                    <span class="cart-item-multiply">x</span>
                                                                    <span class="cart-item-qnt">{cart.quantity}</span>
                                                                </div>
                                                            </div>
                                                            <div class="cart-item-body">
                                                                <span class="cart-item-description">
                                                                    Phân loại: khá
                                                                </span>
                                                                <span class="cart-item-remove">Xóa</span>
                                                            </div>
                                                        </div>
                                                    </li>
                                                </>
                                            ))}
                                        </ul>
                                        <a href="#" class="view-cart btn-cart">
                                            Xem giỏ hàng
                                        </a>
                                    </div>
                                </div>

                                <form className="after-login" onSubmit={handleSubmit}>
                                    <div class="info-account account-ui">
                                        <img
                                            src="https://saigonbling.b-cdn.net/wp-content/uploads/2022/07/ho-ly-la-gi-2.jpg"
                                            className="account-user-avatar"
                                        ></img>
                                        <span class="account-user-name">{username}</span>
                                        <ul class="account-menu">
                                            <li>
                                                <button className="btn-logout" type="submit">
                                                    Đăng xuất
                                                </button>
                                            </li>
                                        </ul>
                                    </div>
                                </form>
                            </>
                        ) : (
                            <>
                                <button className="btn-header btn-login">
                                    <a href="http://localhost:2112/login">Đăng nhập</a>
                                </button>
                                {/* <button className="btn-header btn-sub">Đăng ký</button> */}
                            </>
                        )}
                    </div>
                </div>

                <div className="header-second">
                    <img className="logo" src="./logo.jpg" alt="" />
                    <h3>Mang lợi ích cho không gian xanh</h3>
                    <div className="search">
                        <input
                            // ref = {inputRef}
                            // value={searchValue}
                            placeholder="Search plants"
                            spellCheck={false}
                            // onChange={(e) => setSearchValue(e.target.value)}
                            // onFocus={() => setShowResult(true)}
                        />
                        <button1 className="clear">
                            <FontAwesomeIcon icon={faCircleXmark} />
                        </button1>
                        <FontAwesomeIcon className="loading" icon={faSpinner} />
                        <button1 className="search-btn">
                            <FontAwesomeIcon icon={faMagnifyingGlass} />
                        </button1>
                    </div>
                </div>
                <div className="header-third">
                    <div className="tree-category">
                        {menus.map((menu) => {
                            return (
                                <>
                                    <div className="tree-type">
                                        <img src="sen-da-mini.jpg" alt="" />
                                        <li>
                                            <a href="#">{menu.name}</a>
                                        </li>
                                    </div>
                                </>
                            );
                        })}
                    </div>
                </div>
            </header>
        </>
    );
};

export default Header;
