import { useState, useEffect, useHistory } from 'react';
import { Link, useH } from 'react-router-dom';

function Login() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    // const history = useHistory();

    useEffect(() => {
        // Kiểm tra nếu đã đăng nhập từ trước
        const storedLoginStatus = localStorage.getItem('isLoggedIn');
        if (storedLoginStatus === 'true') {
            setIsLoggedIn(true);
        }
    }, []);

    const handleSubmit = (e) => {
        // Kiểm tra thông tin đăng nhập
    if (username === 'baohoa' && password === '123456') {
        // Đăng nhập thành công
        
        // Cập nhật trạng thái đăng nhập
        setIsLoggedIn(true);

        // Lưu trạng thái đăng nhập vào Local Storage
        localStorage.setItem('isLoggedIn', 'true');

        // Chuyển hướng người dùng về trang chủ
        window.location.href = `http://localhost:3000?username=${username}`;
    } else {
        // Đăng nhập thất bại
        alert('Thông tin đăng nhập không chính xác');
    }
        
      };

    const handleLogin = () => {
        // Thực hiện xác thực đăng nhập và cập nhật trạng thái isLoggedIn

        setIsLoggedIn(true);

        // Lưu trạng thái đăng nhập vào Local Storage
        localStorage.setItem('isLoggedIn', 'true');

        // Chuyển hướng người dùng về trang chủ
        // window.location.href = '/';
        // const username = "example_user";
        // window.location.href = `http://localhost:3000?username=${username}`;
    };

    const handleLogout = () => {
        // Xử lý đăng xuất
        setIsLoggedIn(false);

        // Xóa trạng thái đăng nhập khỏi Local Storage
        localStorage.removeItem('isLoggedIn');
    };

    return (
        <form onSubmit={handleSubmit}>
          <div>
            <label htmlFor="username">Tên đăng nhập:</label>
            <input
              type="text"
              id="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="password">Mật khẩu:</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <button type="submit">Đăng nhập</button>
        </form>
      );
}

export default Login;
