import classNames from "classnames/bind";
import styles from './Button.module.scss';
import {Link} from 'react-router-dom';
const cx = classNames.bind(styles);

function Button({ to, href, className, primary = false, outline = false, rounded = false, small = false, large = false, text = false, disabled = false, leftIcon, rightIcon, children, onClick, ...passProps}) {
    let Comp = 'button';
    const props = {
        onClick,
        ...passProps,
    };

    if(disabled){
        //cachs 1 delete props.onClick;
        Object.keys(props).forEach((key) => {
            if(key.startsWith('on') && typeof props[key] === ' function'){
                delete props[key];
            }
        })
    }

    if(to){
        props.to = to;
        Comp = Link;
        
    } else if ( href){
        props.href = href;
        Comp = 'a';
    }


    const classes = cx('wrapper', {
       [className]: className,
        primary,
        outline,
        small,
        large,
        text,
        disabled,
        rounded,
        leftIcon,
        rightIcon,
    })

    return (
        <Comp className={classes} {...props}>
            {leftIcon && <span className={cx('icon')}>{leftIcon}</span>}
            <span>{children}</span>
            {rightIcon && <span className={cx('icon')}>{rightIcon}</span>}
        </Comp>
    )
}

export default Button;
