import { useEffect, useState } from 'react';

const cartsAPI = 'http://localhost:3000/cart_bought';

// Hook updateCarts - sử dụng để cập nhật giỏ hàng
export function updateCarts() {
  const [carts, setCarts] = useState([]);
  const [cartsUpdated, setCartsUpdated] = useState(false);

  // Tạo kết nối giỏ hàng khi có thay đổi sẽ cập nhật
  useEffect(() => {
    fetch(cartsAPI)
      .then((response) => response.json())
      .then((data) => {
        const filteredData = data.filter((item) => item.owner === id);
        setCarts(filteredData);
      });
  }, [cartsUpdated]);
}

// Hàm function_updateCarts - sử dụng để cập nhật giá trị của cartsUpdated
export function function_updateCarts() {
  setCartsUpdated((prevCartsUpdated) => !prevCartsUpdated);
}
