import React, { useState, useEffect } from "react"
import "./header.scss"
import { menuData } from "../../data-plants"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircleXmark, faMagnifyingGlass, faSpinner } from '@fortawesome/free-solid-svg-icons';
import { useLocation } from 'react-router-dom';

const Header = () => {
    
    const location = useLocation();
    const isLoggedIn = new URLSearchParams(location.search).get('isLoggedIn');
    const [menus, setMenus] = useState(menuData)
    const [checkLoggedIn, setCheckLoggedIn] = useState(false);
    
    useEffect(() => {
        // Kiểm tra nếu đã đăng nhập từ trước
        const storedLoginStatus = localStorage.getItem('isLoggedIn');
        if (storedLoginStatus === 'true') {
          // Xử lý trạng thái đã đăng nhập
          console.log('Đã đăng nhập trang chủ');
        }
      }, []);

    useEffect(() => {
        if (isLoggedIn) {
          setCheckLoggedIn(true);
        } else {
          setCheckLoggedIn(false);
        }
      }, [isLoggedIn]);

    return (
      <>
        <header>
            
            <div className="header-first">
                <ul className="main-menu">
                    {menus.map((menu) => {
                        return (
                        <>    
                            <li><a href={menu.path}>{menu.name}</a></li>
                        </>
                        )
                    })}
                </ul>
                <div className="check-account">
                    {checkLoggedIn ? (
                        <>
                            <h3> đã đăng nhập</h3>
                            <button className="btn-header btn-sub">Đăng xuất</button>
                        </>
                    ):(
                        <>
                            <button className="btn-header btn-login"><a href="http://localhost:3000/login">Đăng nhập</a></button>
                            <button className="btn-header btn-sub">Đăng ký</button>
                        </>
                    )}
                    
                </div>                 
            </div>
            <div className="header-second">
                <img className="logo" src="./logo.jpg" alt="" />
                <h3>Mang lợi ích cho không gian xanh</h3>
                <div className="search">
                    <input 
                        // ref = {inputRef}
                        // value={searchValue}
                        placeholder="Search plants" spellCheck={false} 
                        // onChange={(e) => setSearchValue(e.target.value)}
                        // onFocus={() => setShowResult(true)}
                    />                   
                    <button1 className="clear" >
                        <FontAwesomeIcon icon={faCircleXmark} />
                    </button1>                   
                    <FontAwesomeIcon className="loading" icon={faSpinner} />
                    <button1 className="search-btn">
                        <FontAwesomeIcon icon={faMagnifyingGlass} />
                    </button1>
                </div>
            </div>
            <div className="header-third">
                <div className="tree-category">
                    {menus.map((menu) => {
                        return (
                        <>    
                            <div className="tree-type">
                                <img src="sen-da-mini.jpg" alt="" />
                                <li><a href="#">{menu.name}</a></li>
                            </div>
                            
                        </>
                        )
                    })}
                </div>
                
                
            </div>
        </header>
      </>
    )
  }
  
  export default Header
